import sys
import time

from util.pool import ThreadPool
from util.tcp import Socket
#from guppy.heapy import RM


def send() :
    sock = Socket('localhost', 2000)
    sock.connect()
    
    try:
        
#         message = """"""
        message = 'hello world' 
        # Send data
#         print >>sys.stderr, 'client : sending "%s"' % message
        sock.send(message)
    
        data = sock.receive()
#         print >>sys.stderr, 'client : received "%s"' % str(data)
    
    finally:
#         print >>sys.stderr, 'client : closing socket'
        sock.close()
        
if __name__ == '__main__':
    p = ThreadPool(processes=10)
    while True:
        p.apply_async(send)